<script>

document.addEventListener("DOMContentLoaded", () => {
  const button = document.querySelector('[data-dropdown]');
  const dropdown = document.querySelector('[data-dropdown-items]');
  
  // Initially, hide the dropdown
  dropdown.style.display = 'none';

  button.addEventListener('click', () => {
    const isOpen = dropdown.style.display === 'block';
    dropdown.style.display = isOpen ? 'none' : 'block';

    // Add/remove active class
    button.classList.toggle('bg-gray-200', !isOpen);
    button.classList.toggle('rounded-md', !isOpen);
  });

  document.addEventListener('click', (event) => {
    // Close dropdown if clicked outside
    if (!button.contains(event.target) && !dropdown.contains(event.target)) {
      dropdown.style.display = 'none';
      button.classList.remove('bg-gray-200', 'rounded-md');
    }
  });
});




    
    document.addEventListener('DOMContentLoaded', () => {
        // Custom JavaScript to toggle dropdown
        const toggles = document.querySelectorAll('[data-collapse-toggle]');
        toggles.forEach(toggle => {
            toggle.addEventListener('click', () => {
                const targetId = toggle.getAttribute('aria-controls');
                const target = document.getElementById(targetId);
                if (target.classList.contains('hidden')) {
                    target.classList.remove('hidden');
                } else {
                    target.classList.add('hidden');
                }
            });
        });
    });

    // opent sitebar 
    // JavaScript to toggle the sidebar visibility
function toggleSidebar() {
   const sidebar = document.getElementById('default-sidebar');
   sidebar.classList.toggle('hidden'); // Toggle the hidden class to show/hide the sidebar
}
</script>
